void TurnOnLedFlag(){
	//Turning on and off the led
	GPIOPinWrite(GPIO_PORTF_BASE, GPIO_PIN_1, 0x00);
	SysCtlDelay( SysCtlClockGet() / 9 );
	GPIOPinWrite(GPIO_PORTF_BASE, GPIO_PIN_1, 0x02);
	SysCtlDelay(SysCtlClockGet() / 9 );
	GPIOPinWrite(GPIO_PORTF_BASE, GPIO_PIN_1, 0x00);
	GPIOPinWrite(GPIO_PORTF_BASE, GPIO_PIN_2, 0x04);
	SysCtlDelay(SysCtlClockGet() / 9 );
	GPIOPinWrite(GPIO_PORTF_BASE, GPIO_PIN_2, 0x00);
	GPIOPinWrite(GPIO_PORTF_BASE, GPIO_PIN_3, 0x08);
	SysCtlDelay(SysCtlClockGet() / 9 );
	GPIOPinWrite(GPIO_PORTF_BASE, GPIO_PIN_3, 0x00);
	GPIOPinWrite(GPIO_PORTF_BASE, (GPIO_PIN_2 | GPIO_PIN_1), 0x06);
	SysCtlDelay(SysCtlClockGet() / 9 );
	GPIOPinWrite(GPIO_PORTF_BASE, (GPIO_PIN_1 | GPIO_PIN_2 | GPIO_PIN_3), 0x00);
}

void UnlockPortf(){
	HWREG(GPIO_PORTF_BASE + GPIO_O_LOCK) = GPIO_LOCK_KEY_DD;
	HWREG(GPIO_PORTF_BASE + GPIO_O_CR) |= 0x01;
	HWREG(GPIO_PORTF_BASE + GPIO_O_LOCK) = 0;
}


/*
PF1 Red led 0x02
PF2 Blue led 0x04
PF3 Green led 0x08
*/

void Out(unsigned int i){
}

struct State {
	unsigned int out;
	unsigned short wait;
	unsigned char next[5];
};

typedef const struct State StateType;

//Shortcuts to refer to the various states
#define LEFT2 	0
#define LEFT 	1
#define CENTER 	2
#define RIGHT 	3
#define RIGHT2 	4

// Data structure
StateType Fsm[5] = {
		{0, 300, {LEFT2, LEFT, RIGHT2}},
		{1, 300, {LEFT, CENTER, LEFT2}},
		{2, 300, {CENTER, RIGHT, LEFT}},
		{3, 300, {RIGHT, RIGHT2, CENTER}},
		{4, 300, {RIGHT2, LEFT2, RIGHT}}
};

//Current State Variable
unsigned char cState;

void main(void){

	unsigned char input;

	//Setting clock
	SysCtlClockSet(SYSCTL_SYSDIV_4 | SYSCTL_USE_PLL | SYSCTL_XTAL_16MHZ | SYSCTL_OSC_MAIN);
	//Systick init

	//Init UART
	SysCtlPeripheralEnable(SYSCTL_PERIPH_GPIOA);
	GPIOPinConfigure(GPIO_PA0_U0RX);
	GPIOPinConfigure(GPIO_PA1_U0TX);
	GPIOPinTypeUART(GPIO_PORTA_BASE, GPIO_PIN_0 | GPIO_PIN_1);
	UARTStdioInit(0);

	//Portf Init
	SysCtlPeripheralEnable(SYSCTL_PERIPH_GPIOF); // enable clock port f
	UnlockPortf(); // unlocks port f configure register turns it into GPIO function and locks it again.
	GPIODirModeSet(GPIO_PORTF_BASE, GPIO_PIN_0 | GPIO_PIN_4, GPIO_DIR_MODE_IN); //Set PF0(SW2) and PF4(SW1) as inputs
	GPIOPadConfigSet(GPIO_PORTF_BASE, GPIO_PIN_0 | GPIO_PIN_4, GPIO_STRENGTH_2MA, GPIO_PIN_TYPE_STD_WPU); //Configuring Current strength and pull up resistors

	//Set PF1(Red led) as an output
	GPIOPinTypeGPIOOutput(GPIO_PORTF_BASE, GPIO_PIN_1);
	GPIOPinTypeGPIOOutput(GPIO_PORTF_BASE, GPIO_PIN_2);
	GPIOPinTypeGPIOOutput(GPIO_PORTF_BASE, GPIO_PIN_3);

	// init value for current state variable
	cState = CENTER;

	TurnOnLedFlag();
	UARTprintf("Hello World!** \n");

	while(1){
		UARTprintf("While!!! \n");
		// Generate Output
		//Out();
		switch(Fsm[cState].out){
				case 0:
					Nokia5110_Clear();
					Nokia5110_DrawFullImage(left2_s);
					GPIOPinWrite(GPIO_PORTF_BASE, (GPIO_PIN_1 | GPIO_PIN_2 | GPIO_PIN_3), 0x00);
					GPIOPinWrite(GPIO_PORTF_BASE, GPIO_PIN_1, 0x02); //turns on red led
					UARTprintf("Case 0 \n");
					break;
				case 1:
					Nokia5110_Clear();
					Nokia5110_DrawFullImage(left_s);
					GPIOPinWrite(GPIO_PORTF_BASE, (GPIO_PIN_1 | GPIO_PIN_2 | GPIO_PIN_3), 0x00);
					GPIOPinWrite(GPIO_PORTF_BASE, GPIO_PIN_2, 0x04); // turns on blue led
					UARTprintf("Case 1 \n");
					break;
				case 2:
					Nokia5110_Clear();
					Nokia5110_DrawFullImage(center_s);
					GPIOPinWrite(GPIO_PORTF_BASE, (GPIO_PIN_1 | GPIO_PIN_2 | GPIO_PIN_3), 0x00);
					GPIOPinWrite(GPIO_PORTF_BASE, GPIO_PIN_3, 0x08); // turns on green led
					UARTprintf("Case 2 \n");
					break;
				case 3:
					Nokia5110_Clear();
					Nokia5110_DrawFullImage(right_s);
					GPIOPinWrite(GPIO_PORTF_BASE, (GPIO_PIN_1 | GPIO_PIN_2 | GPIO_PIN_3), 0x00);
					GPIOPinWrite(GPIO_PORTF_BASE, (GPIO_PIN_2 | GPIO_PIN_3), 0x0B); //Turns on blue and green
					UARTprintf("Case 3 \n");
					break;
				case 4:
					Nokia5110_Clear();
					Nokia5110_DrawFullImage(right2_s);
					GPIOPinWrite(GPIO_PORTF_BASE, (GPIO_PIN_1 | GPIO_PIN_2 | GPIO_PIN_3), 0x00);
					UARTprintf("Case 4 \n");
					break;
				default:
					UARTprintf("Default! || \n");
			};
		UARTprintf("Output Generated!-- \n");
		// wait for time relevant to state
		SysCtlDelay(SysCtlClockGet() / Fsm[cState].wait);
		// change state based on input and current state
		input = (!GPIOPinRead(GPIO_PORTF_BASE, GPIO_PIN_4) & 0x10) >> 3 | (!GPIOPinRead(GPIO_PORTF_BASE, GPIO_PIN_0) & 0x01); // adquiring input
		cState = Fsm[cState].next[input];
	}
}




